import React from 'react';
import './Spellfound.css';

const match = (letters, facit) => {
	var m = true;
	facit.map(f => {
		if (f.model != '' && letters[f.model] != f.value)
			m = false
	})
	return m;
}

class Spellfound extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			letters : {}, facit : {},
			matrix : []
		};
		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleChange(event) {
		if (event !== undefined) {
			let lowerCase = event.target.value.toLowerCase();
			this.setState({
				[event.target.name] : lowerCase
			});
			this.state.letters[event.target.name] = lowerCase;
		}
	}

	handleSubmit(event) {
		if (match(this.state.letters, this.state.facit)) {
			alert('Correctomundo!');
		} else {
			alert('Njae...');
		}
		event.preventDefault();
	}

	render() {
		const matrixTab = this.state.matrix.map(rad => {
			return (<tr key={rad[0].id}>
					{rad.map(cell => {
						return (<td key={cell.id}>
							<input disabled={this.getDisabled(cell)} className="sqr" type="text" size="1" maxLength="1" required
										 style={this.getDisabled(cell) ? {backgroundColor : 'black'} : {backgroundColor : 'ivory'}}
										 name={cell.model} value={this.state.letters[cell.model]} onChange={this.handleChange}/>
						</td>)
					})}
				</tr>
			)
		})
		return (<div>
				<h2>{this.state.title}</h2>
				<div class="row">
				<form onSubmit={this.handleSubmit}>
					<table>
						<tbody>
						{matrixTab}
						</tbody>
					</table>
					<input type="submit" value="Kontrollera"/>
				</form>
				<p>{this.state.legend}</p>
				</div>
			</div>
		)
	}

	getDisabled(cell) {
		return cell.model == '';
	}

	componentDidMount() {
		return fetch('http://localhost:3000/puzzles/matrix2.json')
		.then(response => response.json())
		.then((json) => {
			this.setState({matrix : json.matrix, facit : json.facit, title : json.title, legend : json.legend})
		})
		.catch((error) => {
			console.error(error)
		})
	}
}

export default Spellfound;
