import React from 'react';
import './Spellfound.css';

const match = (letters, facit) => {
	var m = true;
	facit.map(f => {
		if (f.model !== '' && letters[f.model].toUpperCase() !== f.value.toUpperCase()) {
			m = false;
		}
		return 0;
	})
	return m;
}

class Spellfound extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			letters : {}, facit : {},
			matrix : []
		};
		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleArrowKey = this.handleArrowKey.bind(this);
	}

	handleChange(event) {
		if (event !== undefined) {
			let upperCase = event.target.value.toUpperCase();
			this.setState({
				[event.target.name] : upperCase
			});
			this.state.letters[event.target.name] = upperCase;
			var nextid = this.nextIdRight(event.target.id);
			this.moveFocus(nextid);
		}
	}

	moveFocus(nextid) {
		if (document.getElementById(nextid) !== null) {
			document.getElementById(nextid).focus();
		}
	}

	nextIdRight(id) {
		return id.substr(0, 3) + (parseInt(id.substr(3, 2), 10) + 1).toString().padStart(2, "0");
	}
	nextIdLeft(id) {
		return id.substr(0, 3) + (parseInt(id.substr(3, 2), 10) - 1).toString().padStart(2, "0");
	}
	nextIdUp(id) {
		return id.substr(0, 1) + (parseInt(id.substr(1, 2), 10) - 1).toString().padStart(2, "0") + id.substr(3, 2);
	}
	nextIdDown(id) {
		return id.substr(0, 1) + (parseInt(id.substr(1, 2), 10) + 1).toString().padStart(2, "0") + id.substr(3, 2);
	}

	handleArrowKey (e) {
		var nextid;
		switch (e.key) {
			case 'ArrowUp':
				nextid = this.nextIdUp(e.target.id);
				break;
			case 'ArrowDown':
				nextid = this.nextIdDown(e.target.id);
				break;
			case 'ArrowLeft':
				nextid = this.nextIdLeft(e.target.id);
				break;
			case 'ArrowRight':
				nextid = this.nextIdRight(e.target.id);
				break;
		}
		this.moveFocus(nextid);
	};

	handleSubmit(event) {
		if (match(this.state.letters, this.state.facit)) {
			alert('Correctomundo!');
		} else {
			alert('Njae...');
		}
		event.preventDefault();
	}

	render() {
		const matrixTab = this.state.matrix.map(rad => {
			return (<tr key={rad[0].id}>
					{rad.map(cell => {
						return (<td key={cell.id}>
							<input id={cell.id}
										 disabled={this.getDisabled(cell)} className="sqr" type="text" size="1" maxLength="1" required
										 style={this.getDisabled(cell) ? {visibility : 'hidden'} : {backgroundColor : 'ivory'}}
										 name={cell.model} value={this.state.letters[cell.model]} onChange={this.handleChange} onKeyUp={this.handleArrowKey}/>
						</td>)
					})}
				</tr>
			)
		})
		return (
			<div>
				<h2 className="title">{this.state.title}</h2>
				<div className="component">
					<form onSubmit={this.handleSubmit}>
						<table>
							<tbody>
							{matrixTab}
							</tbody>
						</table>
						<div className="verify">
							<input type="submit" value="Kontrollera"/>
						</div>
					</form>
				</div>
				<div className="legend">
					<p>{this.state.legend}</p>
				</div>
			</div>
		)
	}

	getDisabled(cell) {
		return cell.model === '';
	}

	componentDidMount() {
		return fetch('http://localhost:3000/puzzles/fokusfjäril.json')
		.then(response => response.json())
		.then((json) => {
			this.setState({matrix : json.matrix, facit : json.facit, title : json.title, legend : json.legend})
		})
		.catch((error) => {
			console.error(error)
		})
	}
}

export default Spellfound;
