import React from 'react';
import './Spellfound.css';

const match = (letters, facit) => {
	var m = true;
	facit.map(f => {
		if (f.model !== '' && letters[f.model].toUpperCase() !== f.value.toUpperCase()) {
			m = false;
		}
		return 0;
	})
	return m;
}

class Spellfound extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			letters : {}, facit : {},
			matrix : []
		};
		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleChange(event) {
		if (event !== undefined) {
			let upperCase = event.target.value.toUpperCase();
			this.setState({
				[event.target.name] : upperCase
			});
			this.state.letters[event.target.name] = upperCase;
			var thisid = event.target.id;
			var nextid = thisid.substr(0,3) + (parseInt(thisid.substr(3,2),10) + 1).toString().padStart(1, "0");
			console.log("next id " + nextid);
			document.getElementById(nextid).focus();
			/*
			var next = event.srcElement || event.target;
			while (next = next.nextElementSibling) {
				if (next == null)
					break;
				if (next.tagName.toLowerCase() === "input") {
					next.focus();
					break;
				}
			}
			*/
		}
	}

	handleSubmit(event) {
		if (match(this.state.letters, this.state.facit)) {
			alert('Correctomundo!');
		} else {
			alert('Njae...');
		}
		event.preventDefault();
	}

	render() {
		const matrixTab = this.state.matrix.map(rad => {
			return (<tr key={rad[0].id}>
					{rad.map(cell => {
						return (<td key={cell.id}>
							<input id={cell.id}
										 disabled={this.getDisabled(cell)} className="sqr" type="text" size="1" maxLength="1" required
										 style={this.getDisabled(cell) ? {visibility : 'hidden'} : {backgroundColor : 'ivory'}}
										 name={cell.model} value={this.state.letters[cell.model]} onChange={this.handleChange}/>
						</td>)
					})}
				</tr>
			)
		})
		return (
			<div>
				<h2 className="title">{this.state.title}</h2>
				<div className="component">
					<form onSubmit={this.handleSubmit}>
						<table>
							<tbody>
							{matrixTab}
							</tbody>
						</table>
						<div className="verify">
							<input type="submit" value="Kontrollera"/>
						</div>
					</form>
				</div>
				<div className="legend">
					<p>{this.state.legend}</p>
				</div>
			</div>
		)
	}

	getDisabled(cell) {
		return cell.model == '';
	}

	componentDidMount() {
		return fetch('http://localhost:3000/puzzles/fokusfjÃ¤ril.json')
		.then(response => response.json())
		.then((json) => {
			this.setState({matrix : json.matrix, facit : json.facit, title : json.title, legend : json.legend})
		})
		.catch((error) => {
			console.error(error)
		})
	}
}

export default Spellfound;
